import React,{useState,useEffect} from "react";
import Axios from '../../node_modules/axios';
import { useHistory,Route,useParams } from "react-router-dom";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../components/Test.css"


function Test(){
    var inteobj = []
    const [axlist, setstate] = useState(inteobj)
    let {id} = useParams();
    let {num} = useParams();
    let next = React.createRef();
    let finish = React.createRef();


    let curnum = parseInt(num)
     let nextpage = 1+curnum
     let strnxt = nextpage.toString()
     let optionlist

    console.log(nextpage)
    console.log(id);
    console.log(num);

    let testquest = []
    let testname
    let options = []
    let type = "Multiple-Response"
    let limit

    useEffect(() => {
    Axios.get("http://interviewapi.stgbuild.com/getQuizData").then((response) => {
    const inlist = response.data.tests
    setstate(inlist)
  })
},[])

  for(let i=0;i<axlist.length;i++)
  {
   if(axlist[i]._id == id)
   {
    testname =axlist[i].name
    limit = axlist[i].questions.length
    testquest = axlist[i].questions[num]
    options = testquest.options
    if(testquest.type == type)
    {
      type = "Multiple-Response"
    }
    else{
        type = "Single" 
    }
    console.log(type)
   }
 }




 if(type == "Single"){
  optionlist = options.map((optn,index) =>
 <div class="radio">
 <input type="radio" name="option" value={"option"+index}/>{optn}
 </div>
 );
 }
 else{
    optionlist = options.map((optn,index) =>
    <div class="checkbox">
    <input type="checkbox" name={"option"+index} value={"option"+index}/>
    <label for={"option"+index}>{optn}</label>
    </div>
    );
 }





<div class="container">
        <div class="row">
        <h1>My Interview Portal</h1>
            <hr/>
            <div class="col-md-12">
                <div class="panel panel-default">
                    <div class="panel-heading">{testname}</div>
                    <div class="panel-body">
                        <form>
                            <label>{testquest.questionText}</label>
                            <div class="radio">
                                <label>
                                    <input type="radio" name="option" value="option1"/> Javascript Framework
                                </label>
                            </div>
                            <div class="radio">
                                <label>
                                    <input type="radio" name="option" value="option2"/> Java Library
                                </label>
                            </div>
                            <div class="radio">
                                <label>
                                    <input type="radio" name="option" value="option2"/> Programming Language
                                </label>
                            </div>
                        </form>
                    </div>
                    <div class="panel-footer">

                        <a href="test.html" class="btn btn-success">Next</a>
                        <a href="finish.html" class="pull-right btn btn-danger">Finish</a>

                    </div>
                </div>
            </div>
        </div>
    </div>








 if(type == "Single")
 {
  return (
    <>
       <div class="container">
        <div class="row">
        <h1 class="mt-1">My Interview Portal</h1>
        <hr/>           
<div class="modal mt-5" style={{display:"block"}}>
  <div class="modal-dialog">
    <div class="modal-content">
      <div class ="modal-header" style={{backgroundColor:"rgb(255, 147, 147)"}}>
        <h5 class="modal-title" >{testname}</h5>
      </div>
      <div class="modal-body" style={{backgroundColor:"rgb(190, 190, 190)"}}>
      <form style={{textAlign:"left"}}>
                            <label style={{fontSize:"large"}}><b>{testquest.questionText}</b></label>
                            <div style={{fontSize:"medium"}}>
                            {optionlist}
                            </div>
                        </form>
      </div>
      <div class="modal-footer" style={{backgroundColor:"rgb(255, 147, 147)"}}>
          {curnum == limit-1 ? <a ref={finish} href={"/finish"} class="pull-right btn btn-danger">Finish</a>
          :<a ref={next} href={"/test/"+id+"/"+strnxt} class="btn btn-success">Next</a>
          }
      </div>
    </div>
  </div>
</div>
    </div>
    </div>
    </>
  );
 }
  else{
    return (
        <>
           <div class="container">
            <div class="row">
            <h1 class="mt-1">My Interview Portal</h1>
            <hr/>           
    <div class="modal mt-5" style={{display:"block"}}>
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header" style={{backgroundColor:"rgb(255, 147, 147)"}}>
            <h5 class="modal-title">{testname}</h5>
          </div>
          <div class="modal-body" style={{backgroundColor:"rgb(190, 190, 190)"}}>
          <form style={{textAlign:"left"}}>
                                <label style={{fontSize:"large"}}><b>{testquest.questionText}</b></label>
                                <div style={{fontSize:"medium"}}>
                                {optionlist}
                                </div>
                            </form>
          </div>
          <div class="modal-footer" style={{backgroundColor:"rgb(255, 147, 147)"}}>
          {curnum == limit-1 ? <a ref={finish} href={"/finish"} class="pull-right btn btn-danger">Finish</a>
          :<a ref={next} href={"/test/"+id+"/"+strnxt} class="btn btn-success">Next</a>
          }
          </div>
        </div>
      </div>
    </div>



        </div>
        </div>
        </>
      );
  }
}

export default Test;
