import React,{useState,useEffect} from "react";
import Axios from '../../node_modules/axios';
import { useNavigate,Route,useParams } from "react-router-dom";


function Test(){
  let history = useNavigate();
    var inteobj = []
    const [axlist, setstate] = useState(inteobj)

    let {id} = useParams();
    let {num} = useParams();
    let next = React.createRef();
    let finish = React.createRef();

    let curnum
    let testindx
    let optionlist

    console.log(id);
    console.log(num);

    let testquest = []
    let testname
    let options = []
    let type = "Multiple-Response"
    let limit

    useEffect(() => {
    Axios.get("http://interviewapi.stgbuild.com/getQuizData").then((response) => {
    const inlist = response.data.tests
    setstate(inlist)
  })
},[])

  for(let i=0;i<axlist.length;i++)
  {
   if(axlist[i]._id == id)
   {
    testindx = i
    testname =axlist[i].name
    limit = axlist[i].questions.length
    for(let j=0;j<limit;j++)
    {
      if(axlist[i].questions[j]._id == num)
      {
        curnum = j
      }
    }
    testquest = axlist[i].questions[curnum]
    options = testquest.options
    if(testquest.type == type)
    {
      type = "Multiple-Response"
    }
    else{
        type = "Single" 
    }
    console.log(type)
   }
 }


 let nextquest = () => {
  let nextpage = axlist[testindx].questions[curnum+1]._id
  console,log
  history("/test/"+id+"/"+nextpage);
 }


 if(type == "Single"){
  optionlist = options.map((optn,index) =>
 <div class="radio">
 <input type="radio" name="option" value={"option"+index}/>{optn}
 </div>
 );
 }
 else{
    optionlist = options.map((optn,index) =>
    <div class="checkbox">
    <input type="checkbox" name={"option"+index} value={"option"+index}/>
    <label for={"option"+index}>{optn}</label>
    </div>
    );
 }




 if(type == "Single")
 {
  return(
    <div class="container">
        <div class="row">
        <h1>My Interview Portal</h1>
            <hr/>
            <div class="col-md-12">
                <div class="panel panel-default">
                    <div class="panel-heading" style={{textAlign:"left"}}>{testname}</div>
                    <div class="panel-body">
                        <form style={{textAlign:"left",paddingLeft:15}}>
                            <label >{testquest.questionText}</label>
                            {optionlist}
                        </form>
                    </div>

                    {curnum == limit-1 ? 
                    <div class="panel-footer" style={{textAlign:"right"}}> 
                    <a ref={finish} href={"/finish"} class="btn btn-danger">Finish</a>
                    </div>
          :
          <div class="panel-footer" style={{textAlign:"left"}}> 
          <a ref={next} onClick={()=>nextquest()} class="btn btn-success">Next</a>
          </div>
          }
            </div>
        </div>
    </div>
    </div>
  );
 }
else{
  return(
    <div class="container">
        <div class="row">
        <h1>My Interview Portal</h1>
            <hr/>
            <div class="col-md-12">
                <div class="panel panel-default">
                    <div class="panel-heading" style={{textAlign:"left"}}>{testname}</div>
                    <div class="panel-body">
                        <form style={{textAlign:"left",paddingLeft:15}}>
                        <label><b>{testquest.questionText}</b></label>
                                <div>
                                {optionlist}
                                </div>
                        </form>
                    </div>

                    {curnum == limit-1 
                    ? <div class="panel-footer"> 
                    <a ref={finish} style={{textAlign:"right"}}href={"/finish"} class="btn btn-danger">Finish</a>
                    </div>
          :    <div class="panel-footer">
            <a ref={next} onClick={()=>nextquest()} style={{textAlign:"left"}} class="btn btn-success">Next</a>
            </div>}
              </div>
        </div>
    </div>
    </div>
  );
}

}

export default Test;
