import React,{useState,useEffect} from "react";
import Axios from '../../node_modules/axios';
import { useNavigate,Route,useParams } from "react-router-dom";


function Test(){
  let history = useNavigate();
    var inteobj = []
    var axlist =[]

    let {id} = useParams();
    let {num} = useParams();
    let testarr = []

    let curnum
    let testindx
    let optionlist

    // console.log(id);
    // console.log(num);

    let testquest = []
    let testname
    let options = []
    let type = "Multiple-Response"
    let limit

    let titleinput = React.createRef();


    useEffect(() => {
    let currtest = localStorage.getItem("test");
    //testarr = JSON.parse(currtest);
    //  axlist = testarr
     console.log(currtest)
},[])

    console.log(axlist)
    testindx = axlist._id
    testname =axlist.name
    limit = axlist.questions.length
    for(let j=0;j<limit;j++)
    {
      if(axlist.questions[j]._id == num)
      {
        curnum = j
      }
    }
    testquest = axlist.questions[curnum]
    options = testquest.options
    if(testquest.type == type)
    {
      type = "Multiple-Response"
    }
    else{
        type = "Single" 
    }
    console.log(type)

 let nextquest = () => {
  let nextpage = axlist.questions[curnum+1]._id
  console.log(nextpage)
  history("/test/"+id+"/"+nextpage);
 }

 let finish = () => {
  history("/finish")
 }


 if(type == "Single"){
  optionlist = options.map((optn,index) =>
 <div class="radio">
 <input type="radio" name="option" value={index}/>{optn}
 </div>
 );
 }
 else{
    optionlist = options.map((optn,index) =>
    <div class="checkbox">
    <input type="checkbox" name={index} value={index}/>
    <label for={index}>{optn}</label>
    </div>
    );
 }




 if(type == "Single")
 {
  return(
    <div class="container">
        <div class="row">
        <h1>My Interview Portal</h1>
            <hr/>
            <div class="col-md-12">
                <div class="panel panel-default">
                    <div class="panel-heading" style={{textAlign:"left"}}>{testname}</div>
                    <div class="panel-body">
                        <form style={{textAlign:"left",paddingLeft:15}}>
                            <label >{testquest.questionText}</label>
                            {optionlist}
                        </form>
                    </div>

                    {curnum == limit-1 ? 
                    <div class="panel-footer" style={{textAlign:"right"}}> 
                    <a onClick={()=>finish()} class="btn btn-danger">Finish</a>
                    </div>
          :
          <div class="panel-footer" style={{textAlign:"left"}}> 
          <a onClick={()=>nextquest()} class="btn btn-success">Next</a>
          </div>
          }
            </div>
        </div>
    </div>
    </div>
  );
 }
else{
  return(
    <div class="container">
        <div class="row">
        <h1>My Interview Portal</h1>
            <hr/>
            <div class="col-md-12">
                <div class="panel panel-default">
                    <div class="panel-heading" style={{textAlign:"left"}}>{testname}</div>
                    <div class="panel-body">
                        <form style={{textAlign:"left",paddingLeft:15}}>
                        <label><b>{testquest.questionText}</b></label>
                                <div>
                                {optionlist}
                                </div>
                        </form>
                    </div>

                    {curnum == limit-1 
                    ? <div class="panel-footer"> 
                    <a style={{textAlign:"right"}} onClick={()=>finish()} class="btn btn-danger">Finish</a>
                    </div>
          :    <div style={{textAlign:"left"}} class="panel-footer">
            <a onClick={()=>nextquest()}  class="btn btn-success">Next</a>
            </div>}
              </div>
        </div>
    </div>
    </div>
  );
}

}

export default Test;
